import { useState } from "react"

const Form = ({firstNameProp, lastNameProp, emailProp, mobileProp, onFormSubmit}) => {

    const [firstName, setFirstName] = useState(firstNameProp);
    const [lastName, setLastName] = useState(lastNameProp);
    const [email, setEmail] = useState(emailProp);
    const [mobile, setMobile] = useState(mobileProp);

    return (
        <form className='form-user'>
            <div className='form-div'>
                <label>First name</label>
                <input type='text' placeholder='Enter firstname' value={firstNameProp} onChange={(e) => {
                    setFirstName(e.target.value)
                    console.log('sadasd ', firstNameProp, firstName);
                }

                }/>
            </div>
            <div className='form-div'>
                <label>Last name</label>
                <input type='text' placeholder='Enter lastname' value={lastName} onChange={(e) => 
                    setLastName(e.target.value)
                }/>
            </div>
            <div className='form-div'>
                <label>Email</label>
                <input type='text' placeholder='Enter email address' value={email} onChange={(e) => 
                    setEmail(e.target.value)
                }/>
            </div>
            <div className='form-div'>
                <label>Mobile No.</label>
                <input type='text' placeholder='Enter mobile number' value={mobile} onChange={(e) => 
                    setMobile(e.target.value)
                }/>
            </div>
            <input type='submit' value='Save'/>
        </form>
    )
}

export default Form
