import './App.css';
import Header from './components/Header'
import Form from './components/Form'
import UserList from './components/UserList'
import { useEffect, useState } from 'react';

function App() {
  const [showAddForm, setShowAddForm] = useState(false)
  const [users, setUsers] = useState([
    {
      id: 1,
      firstName: 'Tamim',
      lastName: 'Iqbal',
      email: 'tamim@test.com',
      mobile: '0142',
      deleted: false,
      editMode: false,
    },
    {
      id: 2,
      firstName: 'Shakib Al',
      lastName: 'Hasan',
      email: 'shakib@test.com',
      mobile: '012342',
      deleted: false,
      editMode: false,
    },
    {
      id: 3,
      firstName: 'Jack',
      lastName: 'Ma',
      email: 'amar_ma@test.com',
      mobile: '014542',
      deleted: false,
      editMode: false,
    },
  ])

  useEffect(() => {
    fetch('/api/users', {
      headers : { 
        'Content-Type': 'application/json',
        'Accept': 'application/json'
       }
    }).then(
      (userResponse) => userResponse.json()).then(
        data => {
          console.log(data);
    })
  })

  const addUserClickHandler = () => {
    setShowAddForm(!showAddForm)
  }

  const userEditClickHandler = (id) => {
    setUsers(users.map(
      (user) => 
        user.id === id ? {...user, editMode: !user.editMode} : user
    ))
  }

  const editFormSubmitHandler = (userInfo) => {
    console.log(userInfo);
    userEditClickHandler(userInfo.id);
  }

  const addUserFormSubmitHandler = (userInfo) => {
    console.log(userInfo);
    addUserClickHandler(userInfo.id);
  }

  const deleteUserHandler = (id) => {
    console.log(id);
    setUsers(users.map(
      (user) => 
        user.id === id ? {...user, deleted: !user.deleted} : user
    ))
  }

  return (
    <div className="container">
      <Header name='Dashboard' showForm={showAddForm} buttonClickHandler={addUserClickHandler}/>
      {showAddForm && <Form onFormSubmit = {addUserFormSubmitHandler} />}
      <UserList 
        users={users} 
        onClickUserEdit = {userEditClickHandler}
        editUserSubmitHandler = {editFormSubmitHandler}
        onDelete = {deleteUserHandler} />
    </div>
  );
}

export default App;
